import React from "react";
import "./App.css";

const App = () =>
{

  const [todos, setTodos] = React.useState([]);
  const [todoName, setTodoName] = React.useState("");
  const [todoEditing, setTodoEditing] = React.useState(null);
  const [editingText, setEditingText] = React.useState("");

  React.useEffect(() =>
  {
    const json = localStorage.getItem("todos");
    const loadedTodos = JSON.parse(json);
    if (loadedTodos)
    {
      setTodos(loadedTodos);
    }
  }, []);

  React.useEffect(() =>
  {
    const json = JSON.stringify(todos);
    localStorage.setItem("todos", json);
  }, [todos]);

  function handleSubmit(e)
  {
    e.preventDefault();

    const newTodo = {
      id: new Date().getTime(),
      name: todoName.name,
      // latitude: todo.latitude,
      // longitude: todo.longitude,
      completed: false
    };
    setTodos([...todos].concat(newTodo));
    todoName("");
  }

  function deleteTodo(id)
  {
    let updatedTodos = [...todos].filter((todo) => todo.id !== id);
    setTodos(updatedTodos);
  }

  function toggleComplete(id)
  {
    let updatedTodos = [...todos].map((todo) =>
    {
      if (todo.id === id)
      {
        todo.completed = !todo.completed;
      }
      return todo;
    });
    setTodos(updatedTodos);
  }

  function submitEdits(id)
  {
    const updatedTodos = [...todos].map((todo) =>
    {
      if (todo.id === id)
      {
        todo.text = editingText;
      }
      return todo;
    });
    setTodos(updatedTodos);
    setTodoEditing(null);
  }

  return (
    <div id="todo-list">
      <h1>Todo List</h1>
      <form onSubmit={handleSubmit}>
        Name:
        <input type="text" onChange={(e) => setTodoName(e.target.value)} value={todoName} />
        Latitude:
        {/* <input type="text" onChange={(e) => setTodo(e.target.value)} value={todo.latitude} />
        Longitude:
        <input type="text" onChange={(e) => setTodo(e.target.value)} value={todo.longitude} /> */}
        <button type="submit">Add Todo</button>
      </form>

      {todos.map((todo) => (
        <div key={todo.id} className="todo">
          <div className="todo-text">
            <input type="checkbox" id="completed" checked={todo.completed} onChange={() => toggleComplete(todo.id)} />
            {todo.id === todoEditing ? (<input type="text" onChange={(e) => setEditingText(e.target.value)} />
            ) : (
              <div>{todo.name}</div>
            )}
          </div>
          <div className="todo-actions">
            {todo.id === todoEditing ? (
              <button onClick={() => submitEdits(todo.id)}>Submit Edits</button>
            ) : (
              <button onClick={() => setTodoEditing(todo.id)}>Edit</button>
            )}
            {/* <div>{todo.latitude}</div>
            <div>{todo.longitude}</div> */}


            <button onClick={() => deleteTodo(todo.id)}>Delete</button>
          </div>
        </div>
      ))}
    </div>
  );
};

export default App;
