import React, { useState } from 'react';
import EditTask from '../modals/EditTask'

const Card = ({ taskObj, index, deleteTask, updateListArray }) =>
{
    const [modal, setModal] = useState(false);

    const colors = [
        {
            primaryColor: "#5D93E1",
            secondaryColor: "#ECF3FC"
        },
        {
            primaryColor: "#F9D288",
            secondaryColor: "#FEFAF1"
        },
        {
            primaryColor: "#5DC250",
            secondaryColor: "#F2FAF1"
        },
        {
            primaryColor: "#F48687",
            secondaryColor: "#FDF1F1"
        },
        {
            primaryColor: "#B964F7",
            secondaryColor: "#F3F0FD"
        }
    ]

    const toggle = () =>
    {
        setModal(!modal);
    }

    const updateTask = (obj) =>
    {
        updateListArray(obj, index)
    }

    const handleDelete = () =>
    {
        deleteTask(index)
    }

    const Locate = () =>
    {
        console.log(taskObj);
    }

    return (
        <div class="card justify-content-center w-100 mt-2 md-2">
            <div class="card-top" style={{ "background-color": colors[index % 5].primaryColor }}></div>
            <div class="task-holder">
                <span class="card-header" style={{ "background-color": colors[index % 5].secondaryColor, "border-radius": "10px" }}>{taskObj.Name}</span>
                <div><p className="mt-3">{taskObj.Description}</p></div>
                <div><p className="mt-1">{taskObj.Latitude}</p></div>
                <div><p className="mt-1">{taskObj.Longitude}</p></div>

                <div class="container justify-content-space">
                    <button class="btn btn-primary" onClick={Locate}>Locate</button>
                    <button class="btn btn-primary" onClick={() => setModal(true)}>Edit</button>
                    <button class="btn btn-primary" onClick={handleDelete}>Delete</button>
                </div>
            </div>
            <EditTask modal={modal} toggle={toggle} updateTask={updateTask} taskObj={taskObj} />
        </div>
    );
};

export default Card;
